//personal
// from https://github.com/ethereum/go-ethereum/blob/5f8e5a487588bab49fb3c9321fd8903132426c47/rpc/api/personal_js.go
web3._extend({
	property: 'personal',
	methods:
	[
		new web3._extend.Method({
			name: 'listAccounts',
			call: 'personal_listAccounts',
			params: 0,
			inputFormatter: [],
			outputFormatter: function(obj) { return obj; }
		}),
		new web3._extend.Method({
			name: 'newAccount',
			call: 'personal_newAccount',
			params: 1,
			inputFormatter: [web3._extend.formatters.formatInputString],
			outputFormatter: web3._extend.formatters.formatOutputString
		}),
		new web3._extend.Method({
			name: 'unlockAccount',
			call: 'personal_unlockAccount',
			params: 3,
			inputFormatter: [web3._extend.formatters.formatInputString,web3._extend.formatters.formatInputString,web3._extend.formatters.formatInputInt],
			outputFormatter: web3._extend.formatters.formatOutputBool
		})
	],
	properties:
	[
	]
});


module.exports = {
  web3:web3,
}
